import React, { useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Box,
  Avatar,
  Menu,
  MenuItem,
  Chip,
  Badge,
  Divider,
  useTheme,
  alpha,
  Slide,
  useScrollTrigger,
} from '@mui/material';
import {
  Nature as NatureIcon,
  Dashboard as DashboardIcon,
  Add as AddIcon,
  Store as MarketplaceIcon,
  AccountBalance as CarbonIcon,
  Payment as PaymentIcon,
  Assessment as ReportsIcon,
  Notifications as NotificationsIcon,
  Person as PersonIcon,
  Logout as LogoutIcon,
  AdminPanelSettings as AdminIcon,
} from '@mui/icons-material';

function HideOnScroll({ children }) {
  const trigger = useScrollTrigger();

  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {children}
    </Slide>
  );
}

const ModernNavbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const theme = useTheme();
  
  const [anchorEl, setAnchorEl] = useState(null);
  const [userInfo, setUserInfo] = useState(() => {
    const stored = localStorage.getItem('userInfo');
    return stored ? JSON.parse(stored) : null;
  });

  const handleMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
  };

  const handleLogout = () => {
    localStorage.removeItem('userInfo');
    localStorage.removeItem('auth_token');
    navigate('/login');
    handleMenuClose();
  };

  const navItems = [
    { 
      label: 'Dashboard', 
      path: '/dashboard', 
      icon: <DashboardIcon />,
      gradient: 'linear-gradient(45deg, #667eea 0%, #764ba2 100%)'
    },
    { 
      label: 'Create Project', 
      path: '/projects/create', 
      icon: <AddIcon />,
      gradient: 'linear-gradient(45deg, #f093fb 0%, #f5576c 100%)'
    },
    { 
      label: 'Marketplace', 
      path: '/marketplace', 
      icon: <MarketplaceIcon />,
      gradient: 'linear-gradient(45deg, #4facfe 0%, #00f2fe 100%)'
    },
    { 
      label: 'Carbon Credits', 
      path: '/carbon-credits', 
      icon: <CarbonIcon />,
      gradient: 'linear-gradient(45deg, #43e97b 0%, #38f9d7 100%)'
    },
    { 
      label: 'Payments', 
      path: '/payments', 
      icon: <PaymentIcon />,
      gradient: 'linear-gradient(45deg, #fa709a 0%, #fee140 100%)'
    },
    { 
      label: 'Reports', 
      path: '/reports', 
      icon: <ReportsIcon />,
      gradient: 'linear-gradient(45deg, #a8edea 0%, #fed6e3 100%)'
    },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <HideOnScroll>
      <AppBar 
        position="fixed" 
        elevation={0}
        sx={{
          background: 'linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(248, 250, 255, 0.95) 100%)',
          backdropFilter: 'blur(20px)',
          borderBottom: '1px solid rgba(255, 255, 255, 0.2)',
          color: theme.palette.text.primary,
        }}
      >
        <Toolbar sx={{ justifyContent: 'space-between', px: { xs: 2, md: 4 } }}>
          {/* Logo Section */}
          <Box 
            sx={{ 
              display: 'flex', 
              alignItems: 'center', 
              cursor: 'pointer',
              transition: 'transform 0.3s ease',
              '&:hover': {
                transform: 'scale(1.05)',
              }
            }}
            onClick={() => navigate('/dashboard')}
          >
            <Avatar
              sx={{
                background: 'linear-gradient(45deg, #0066CC 30%, #00BFA5 90%)',
                mr: 2,
                width: 48,
                height: 48,
                boxShadow: '0 8px 32px rgba(0, 102, 204, 0.3)',
              }}
            >
              <NatureIcon sx={{ fontSize: 28 }} />
            </Avatar>
            <Box>
              <Typography 
                variant="h6" 
                component="div" 
                sx={{ 
                  fontWeight: 700,
                  background: 'linear-gradient(45deg, #0066CC 30%, #00BFA5 90%)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                }}
              >
                Blue Carbon MRV
              </Typography>
              <Typography variant="caption" color="textSecondary">
                Coastal Ecosystem Platform
              </Typography>
            </Box>
          </Box>

          {/* Navigation Items */}
          <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 1 }}>
            {navItems.map((item) => (
              <Button
                key={item.path}
                startIcon={item.icon}
                onClick={() => navigate(item.path)}
                sx={{
                  borderRadius: 3,
                  px: 3,
                  py: 1,
                  textTransform: 'none',
                  fontWeight: 500,
                  position: 'relative',
                  background: isActive(item.path) 
                    ? item.gradient
                    : 'transparent',
                  color: isActive(item.path) 
                    ? 'white'
                    : theme.palette.text.primary,
                  '&:hover': {
                    background: isActive(item.path)
                      ? item.gradient
                      : alpha(theme.palette.primary.main, 0.1),
                    transform: 'translateY(-2px)',
                  },
                  '&::before': {
                    content: '""',
                    position: 'absolute',
                    bottom: -8,
                    left: '50%',
                    transform: 'translateX(-50%)',
                    width: isActive(item.path) ? '80%' : '0%',
                    height: 3,
                    background: item.gradient,
                    borderRadius: 2,
                    transition: 'width 0.3s ease',
                  },
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                }}
              >
                {item.label}
              </Button>
            ))}
          </Box>

          {/* User Section */}
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <IconButton
              sx={{
                background: 'linear-gradient(45deg, #667eea 30%, #764ba2 90%)',
                color: 'white',
                '&:hover': {
                  transform: 'scale(1.1)',
                  boxShadow: '0 8px 32px rgba(102, 126, 234, 0.4)',
                }
              }}
            >
              <Badge badgeContent={3} color="error">
                <NotificationsIcon />
              </Badge>
            </IconButton>

            <Box
              sx={{
                display: 'flex',
                alignItems: 'center',
                gap: 1.5,
                cursor: 'pointer',
                p: 1,
                borderRadius: 3,
                transition: 'all 0.3s ease',
                '&:hover': {
                  background: alpha(theme.palette.primary.main, 0.05),
                }
              }}
              onClick={handleMenuOpen}
            >
              <Avatar
                sx={{
                  width: 40,
                  height: 40,
                  background: userInfo?.userType === 'admin'
                    ? 'linear-gradient(45deg, #fa709a 0%, #fee140 100%)'
                    : 'linear-gradient(45deg, #4facfe 0%, #00f2fe 100%)',
                  boxShadow: '0 4px 20px rgba(0, 0, 0, 0.2)',
                }}
              >
                {userInfo?.userType === 'admin' ? <AdminIcon /> : <PersonIcon />}
              </Avatar>
              
              <Box sx={{ display: { xs: 'none', md: 'block' } }}>
                <Typography variant="subtitle2" fontWeight={600}>
                  {userInfo?.name || 'User'}
                </Typography>
                <Chip
                  label={userInfo?.userType === 'admin' ? 'Admin' : 'Community'}
                  size="small"
                  sx={{
                    height: 20,
                    fontSize: '0.7rem',
                    background: userInfo?.userType === 'admin'
                      ? 'linear-gradient(45deg, #fa709a 0%, #fee140 100%)'
                      : 'linear-gradient(45deg, #4facfe 0%, #00f2fe 100%)',
                    color: 'white',
                    fontWeight: 500,
                  }}
                />
              </Box>
            </Box>

            <Menu
              anchorEl={anchorEl}
              open={Boolean(anchorEl)}
              onClose={handleMenuClose}
              PaperProps={{
                sx: {
                  borderRadius: 3,
                  minWidth: 200,
                  background: 'linear-gradient(135deg, #ffffff 0%, #f8faff 100%)',
                  boxShadow: '0 16px 48px rgba(0, 0, 0, 0.1)',
                  border: '1px solid rgba(255, 255, 255, 0.2)',
                  backdropFilter: 'blur(10px)',
                }
              }}
              transformOrigin={{ horizontal: 'right', vertical: 'top' }}
              anchorOrigin={{ horizontal: 'right', vertical: 'bottom' }}
            >
              <MenuItem sx={{ py: 2 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, width: '100%' }}>
                  <Avatar
                    sx={{
                      width: 32,
                      height: 32,
                      background: userInfo?.userType === 'admin'
                        ? 'linear-gradient(45deg, #fa709a 0%, #fee140 100%)'
                        : 'linear-gradient(45deg, #4facfe 0%, #00f2fe 100%)',
                    }}
                  >
                    {userInfo?.userType === 'admin' ? <AdminIcon /> : <PersonIcon />}
                  </Avatar>
                  <Box>
                    <Typography variant="subtitle2" fontWeight={600}>
                      {userInfo?.email}
                    </Typography>
                    <Typography variant="caption" color="textSecondary">
                      {userInfo?.userType === 'admin' ? 'Administrator' : 'Community User'}
                    </Typography>
                  </Box>
                </Box>
              </MenuItem>
              
              <Divider />
              
              <MenuItem onClick={handleLogout} sx={{ py: 1.5 }}>
                <LogoutIcon sx={{ mr: 2, color: 'error.main' }} />
                <Typography color="error">Logout</Typography>
              </MenuItem>
            </Menu>
          </Box>
        </Toolbar>
      </AppBar>
    </HideOnScroll>
  );
};

export default ModernNavbar;
