import React, { useState } from 'react';
import {
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Box,
  Avatar,
  Typography,
  Divider,
  useTheme,
  Badge,
} from '@mui/material';
import {
  Dashboard as DashboardIcon,
  Eco as EcoIcon,
  Assignment as ProjectsIcon,
  BarChart as AnalyticsIcon,
  Payment as PaymentIcon,
  VerifiedUser as VerificationIcon,
  Assessment as ReportsIcon,
  Settings as SettingsIcon,
  AccountCircle as ProfileIcon,
  Notifications as NotificationsIcon,
  ExitToApp as LogoutIcon,
} from '@mui/icons-material';
import { useNavigate, useLocation } from 'react-router-dom';

const SIDEBAR_WIDTH = 280;

const ModernSidebar = ({ user, onLogout }) => {
  const theme = useTheme();
  const navigate = useNavigate();
  const location = useLocation();
  const [hoveredItem, setHoveredItem] = useState(null);

  const getMenuItems = () => {
    const baseItems = [
      { id: 'dashboard', label: 'Dashboard', icon: <DashboardIcon />, path: '/dashboard' },
      { id: 'projects', label: 'Projects', icon: <ProjectsIcon />, path: '/projects' },
      { id: 'verification', label: 'Verification', icon: <VerificationIcon />, path: '/verification' },
      { id: 'marketplace', label: 'Marketplace', icon: <EcoIcon />, path: '/marketplace' },
      { id: 'reports', label: 'Reports', icon: <ReportsIcon />, path: '/reports' },
    ];

    if (user?.role === 'admin') {
      return [
        ...baseItems,
        { id: 'analytics', label: 'Analytics', icon: <AnalyticsIcon />, path: '/admin/analytics' },
        { id: 'payments', label: 'Payment Distribution', icon: <PaymentIcon />, path: '/admin/payments' },
        { id: 'admin-projects', label: 'Manage Projects', icon: <ProjectsIcon />, path: '/admin/projects' },
      ];
    }

    return baseItems;
  };

  const handleNavigation = (path) => {
    navigate(path);
  };

  const isActiveRoute = (path) => {
    return location.pathname === path || 
           (path === '/dashboard' && location.pathname === '/');
  };

  const menuItems = getMenuItems();

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: SIDEBAR_WIDTH,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: SIDEBAR_WIDTH,
          boxSizing: 'border-box',
          background: theme.palette.background.sidebar,
          borderRight: 'none',
          boxShadow: '4px 0 20px rgba(0, 0, 0, 0.1)',
        },
      }}
    >
      <Box sx={{ overflow: 'auto', height: '100%' }}>
        {/* Logo/Brand Section */}
        <Box
          sx={{
            p: 3,
            textAlign: 'center',
            borderBottom: `1px solid ${theme.palette.grey[700]}`,
            mb: 2,
          }}
        >
          <Box
            sx={{
              width: 50,
              height: 50,
              borderRadius: '12px',
              background: 'linear-gradient(135deg, #4F46E5 0%, #06B6D4 100%)',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              mx: 'auto',
              mb: 2,
            }}
          >
            <EcoIcon sx={{ color: 'white', fontSize: 28 }} />
          </Box>
          <Typography
            variant="h6"
            sx={{
              color: theme.palette.text.sidebar,
              fontWeight: 600,
              fontSize: '1.1rem',
            }}
          >
            Blue Carbon MRV
          </Typography>
          <Typography
            variant="body2"
            sx={{
              color: theme.palette.text.sidebarSecondary,
              fontSize: '0.75rem',
              mt: 0.5,
            }}
          >
            Coastal Restoration Platform
          </Typography>
        </Box>

        {/* User Profile Section */}
        <Box
          sx={{
            px: 3,
            mb: 3,
          }}
        >
          <Box
            sx={{
              display: 'flex',
              alignItems: 'center',
              p: 2,
              borderRadius: '12px',
              background: 'rgba(255, 255, 255, 0.05)',
              border: '1px solid rgba(255, 255, 255, 0.1)',
              transition: 'all 0.3s ease',
              cursor: 'pointer',
              '&:hover': {
                background: 'rgba(255, 255, 255, 0.08)',
                transform: 'translateY(-1px)',
              },
            }}
            onClick={() => handleNavigation('/profile')}
          >
            <Avatar
              sx={{
                width: 40,
                height: 40,
                background: 'linear-gradient(135deg, #06B6D4 0%, #4F46E5 100%)',
                mr: 2,
              }}
            >
              {user?.name?.charAt(0)?.toUpperCase() || 'U'}
            </Avatar>
            <Box sx={{ flex: 1, minWidth: 0 }}>
              <Typography
                variant="subtitle2"
                sx={{
                  color: theme.palette.text.sidebar,
                  fontWeight: 600,
                  fontSize: '0.875rem',
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap',
                }}
              >
                {user?.name || 'User'}
              </Typography>
              <Typography
                variant="body2"
                sx={{
                  color: theme.palette.text.sidebarSecondary,
                  fontSize: '0.75rem',
                  textTransform: 'capitalize',
                }}
              >
                {user?.role || 'Member'}
              </Typography>
            </Box>
            <Badge
              color="success"
              variant="dot"
              sx={{
                '& .MuiBadge-dot': {
                  width: 8,
                  height: 8,
                  borderRadius: '50%',
                },
              }}
            />
          </Box>
        </Box>

        {/* Navigation Menu */}
        <List sx={{ px: 2 }}>
          {menuItems.map((item) => (
            <ListItem key={item.id} disablePadding sx={{ mb: 0.5 }}>
              <ListItemButton
                onClick={() => handleNavigation(item.path)}
                onMouseEnter={() => setHoveredItem(item.id)}
                onMouseLeave={() => setHoveredItem(null)}
                sx={{
                  borderRadius: '12px',
                  py: 1.5,
                  px: 2,
                  transition: 'all 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
                  position: 'relative',
                  overflow: 'hidden',
                  ...(isActiveRoute(item.path) && {
                    background: 'linear-gradient(135deg, #4F46E5 0%, #06B6D4 100%)',
                    color: 'white',
                    boxShadow: '0 4px 12px rgba(79, 70, 229, 0.3)',
                    '&::before': {
                      content: '""',
                      position: 'absolute',
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: 'rgba(255, 255, 255, 0.1)',
                      borderRadius: '12px',
                    },
                  }),
                  ...(!isActiveRoute(item.path) && {
                    '&:hover': {
                      background: theme.palette.background.sidebarHover,
                      transform: 'translateX(4px)',
                      '& .MuiListItemIcon-root': {
                        transform: 'scale(1.1)',
                      },
                    },
                  }),
                }}
              >
                <ListItemIcon
                  sx={{
                    minWidth: 40,
                    color: isActiveRoute(item.path) 
                      ? 'white' 
                      : theme.palette.text.sidebarSecondary,
                    transition: 'all 0.3s ease',
                    '& .MuiSvgIcon-root': {
                      fontSize: '1.25rem',
                    },
                  }}
                >
                  {item.icon}
                </ListItemIcon>
                <ListItemText
                  primary={item.label}
                  primaryTypographyProps={{
                    fontSize: '0.875rem',
                    fontWeight: isActiveRoute(item.path) ? 600 : 500,
                    color: isActiveRoute(item.path) 
                      ? 'white' 
                      : theme.palette.text.sidebar,
                  }}
                />
                {hoveredItem === item.id && !isActiveRoute(item.path) && (
                  <Box
                    sx={{
                      width: 4,
                      height: 20,
                      background: 'linear-gradient(135deg, #4F46E5 0%, #06B6D4 100%)',
                      borderRadius: '2px',
                      transition: 'all 0.3s ease',
                    }}
                  />
                )}
              </ListItemButton>
            </ListItem>
          ))}
        </List>

        {/* Bottom Section */}
        <Box sx={{ mt: 'auto', px: 2, pb: 3 }}>
          <Divider sx={{ my: 2, borderColor: theme.palette.grey[700] }} />
          
          {/* Settings */}
          <ListItem disablePadding sx={{ mb: 0.5 }}>
            <ListItemButton
              onClick={() => handleNavigation('/settings')}
              sx={{
                borderRadius: '12px',
                py: 1.5,
                px: 2,
                transition: 'all 0.3s ease',
                '&:hover': {
                  background: theme.palette.background.sidebarHover,
                  transform: 'translateX(4px)',
                },
              }}
            >
              <ListItemIcon
                sx={{
                  minWidth: 40,
                  color: theme.palette.text.sidebarSecondary,
                }}
              >
                <SettingsIcon />
              </ListItemIcon>
              <ListItemText
                primary="Settings"
                primaryTypographyProps={{
                  fontSize: '0.875rem',
                  fontWeight: 500,
                  color: theme.palette.text.sidebar,
                }}
              />
            </ListItemButton>
          </ListItem>

          {/* Logout */}
          <ListItem disablePadding>
            <ListItemButton
              onClick={onLogout}
              sx={{
                borderRadius: '12px',
                py: 1.5,
                px: 2,
                transition: 'all 0.3s ease',
                '&:hover': {
                  background: 'rgba(239, 68, 68, 0.1)',
                  '& .MuiListItemIcon-root': {
                    color: '#EF4444',
                  },
                  '& .MuiListItemText-primary': {
                    color: '#EF4444',
                  },
                },
              }}
            >
              <ListItemIcon
                sx={{
                  minWidth: 40,
                  color: theme.palette.text.sidebarSecondary,
                  transition: 'color 0.3s ease',
                }}
              >
                <LogoutIcon />
              </ListItemIcon>
              <ListItemText
                primary="Logout"
                primaryTypographyProps={{
                  fontSize: '0.875rem',
                  fontWeight: 500,
                  color: theme.palette.text.sidebar,
                  transition: 'color 0.3s ease',
                }}
              />
            </ListItemButton>
          </ListItem>
        </Box>
      </Box>
    </Drawer>
  );
};

export default ModernSidebar;
