import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Box,
  LinearProgress,
  Chip,
  Button,
  Paper,
  Alert,
  CircularProgress,
} from '@mui/material';
import {
  TrendingUp as TrendingUpIcon,
  Eco as EcoIcon,
  AccountBalance as ProjectIcon,
  Store as MarketIcon,
} from '@mui/icons-material';
import { useSnackbar } from 'notistack';

const Dashboard = () => {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const [stats, setStats] = useState({
    totalProjects: 0,
    totalCredits: 0,
    verifiedProjects: 0,
    pendingVerification: 0
  });
  const [loading, setLoading] = useState(true);
  const [backendStatus, setBackendStatus] = useState('checking');

  useEffect(() => {
    // Test backend connection and fetch dashboard statistics
    const fetchDashboardData = async () => {
      try {
        setLoading(true);

        // Test Python backend connection
        const statusResponse = await fetch('/api/status');
        if (statusResponse.ok) {
          const statusData = await statusResponse.json();
          setBackendStatus('connected');
          enqueueSnackbar('Connected to Python backend successfully!', { variant: 'success' });

          // Fetch projects data
          const projectsResponse = await fetch('/api/projects');
          if (projectsResponse.ok) {
            const projectsData = await projectsResponse.json();
            setStats({
              totalProjects: projectsData.projects?.length || 23,
              totalCredits: 1250,
              verifiedProjects: 18,
              pendingVerification: 5
            });
          }
        } else {
          throw new Error('Backend connection failed');
        }
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        setBackendStatus('disconnected');
        // Use mock data when backend is not available
        setStats({
          totalProjects: 23,
          totalCredits: 1250,
          verifiedProjects: 18,
          pendingVerification: 5
        });
        enqueueSnackbar('Using offline data - Python backend not connected', { variant: 'warning' });
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, [enqueueSnackbar]);

  const statCards = [
    {
      title: 'Total Projects',
      value: stats.totalProjects,
      icon: <ProjectIcon sx={{ fontSize: 40 }} />,
      color: '#1976d2',
      change: '+12%'
    },
    {
      title: 'Carbon Credits',
      value: `${stats.totalCredits} tCO‚ÇÇ`,
      icon: <EcoIcon sx={{ fontSize: 40 }} />,
      color: '#2e7d32',
      change: '+8%'
    },
    {
      title: 'Verified Projects',
      value: stats.verifiedProjects,
      icon: <TrendingUpIcon sx={{ fontSize: 40 }} />,
      color: '#ed6c02',
      change: '+5%'
    },
    {
      title: 'Market Value',
      value: '$45,680',
      icon: <MarketIcon sx={{ fontSize: 40 }} />,
      color: '#9c27b0',
      change: '+15%'
    }
  ];

  const handleQuickAction = (action) => {
    switch(action) {
      case 'create':
        navigate('/projects/create');
        break;
      case 'data':
        navigate('/data-collection');
        break;
      case 'marketplace':
        navigate('/marketplace');
        break;
      default:
        break;
    }
  };

  return (
    <Container maxWidth="lg" sx={{ mt: 4, mb: 4 }}>
      <Box sx={{ mb: 3 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          üåä Blue Carbon MRV Dashboard
        </Typography>

        <Typography variant="subtitle1" color="text.secondary" gutterBottom>
          Welcome to your Community Blue Carbon Monitoring, Reporting & Verification Portal
        </Typography>

        {/* Backend Status */}
        <Alert 
          severity={backendStatus === 'connected' ? 'success' : backendStatus === 'disconnected' ? 'warning' : 'info'}
          sx={{ mb: 2 }}
        >
          {backendStatus === 'connected' && 'Python Backend: Connected ‚úÖ'}
          {backendStatus === 'disconnected' && 'Python Backend: Offline - Using demo data ‚ö†Ô∏è'}
          {backendStatus === 'checking' && (
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <CircularProgress size={16} />
              Connecting to Python backend...
            </Box>
          )}
        </Alert>
      </Box>

      {loading && <LinearProgress sx={{ mb: 3 }} />}

      <Grid container spacing={3}>
        {/* Statistics Cards */}
        {statCards.map((card, index) => (
          <Grid item xs={12} sm={6} md={3} key={index}>
            <Card sx={{ height: '100%', transition: 'transform 0.2s', '&:hover': { transform: 'translateY(-4px)' } }}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <Box sx={{ color: card.color, mr: 2 }}>
                    {card.icon}
                  </Box>
                  <Box>
                    <Typography color="text.secondary" variant="body2">
                      {card.title}
                    </Typography>
                    <Typography variant="h6" component="div" sx={{ fontWeight: 'bold' }}>
                      {card.value}
                    </Typography>
                    <Chip 
                      label={card.change} 
                      color="success" 
                      size="small" 
                      sx={{ mt: 1 }}
                    />
                  </Box>
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}

        {/* Recent Activity */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom sx={{ color: 'primary.main', fontWeight: 'bold' }}>
              üìä Recent Activity
            </Typography>
            <Box sx={{ mt: 2 }}>
              {[
                { action: 'üå± New mangrove restoration project submitted in Sundarbans', time: '2 hours ago' },
                { action: '‚úÖ Carbon credit verification completed for Project #18', time: '5 hours ago' },
                { action: 'üåø 120 tCO‚ÇÇ credits issued for seagrass conservation', time: '1 day ago' },
                { action: 'üõ∞Ô∏è Satellite data uploaded for monitoring validation', time: '2 days ago' },
                { action: 'üí∞ $5,200 earned from carbon credit sales', time: '3 days ago' }
              ].map((item, index) => (
                <Box key={index} sx={{ py: 1, borderBottom: index < 4 ? '1px solid #f0f0f0' : 'none' }}>
                  <Typography variant="body2" color="text.primary">
                    {item.action}
                  </Typography>
                  <Typography variant="caption" color="text.secondary">
                    {item.time}
                  </Typography>
                </Box>
              ))}
            </Box>
          </Paper>
        </Grid>

        {/* Quick Actions */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom sx={{ color: 'primary.main', fontWeight: 'bold' }}>
