import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Alert,
  Card,
  CardContent,
  Grid,
  Divider,
  Chip,
  CircularProgress
} from '@mui/material';
import {
  Nature as NatureIcon,
  Person as UserIcon,
  AdminPanelSettings as AdminIcon,
  Login as LoginIcon
} from '@mui/icons-material';

const Login = () => {
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [userType, setUserType] = useState('user');
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // Simple user/admin login only
      await new Promise(resolve => setTimeout(resolve, 1000));

      if (formData.email && formData.password) {
        const userData = {
          email: formData.email,
          userType: userType,
          role: userType,
          loginTime: new Date().toISOString(),
          name: userType === 'admin' ? 'NCCR Administrator' : 'Project Manager'
        };

        localStorage.setItem('auth_user', JSON.stringify(userData));
        localStorage.setItem('auth_token', `demo_token_${userType}_${Date.now()}`);

        setMessage(`Login successful! Welcome, ${userData.name}`);

        setTimeout(() => {
          if (userType === 'admin') {
            navigate('/admin/dashboard');
          } else {
            navigate('/dashboard');
          }
        }, 1000);
      } else {
        setMessage('Please enter email and password');
      }
    } catch (error) {
      console.error('Login error:', error);
      setMessage('Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = (demoUserType) => {
    setUserType(demoUserType);
    if (demoUserType === 'admin') {
      setFormData({
        email: 'admin@nccr.gov.in',
        password: 'admin123'
      });
    } else {
      setFormData({
        email: 'user@community.org',
        password: 'user123'
      });
    }
  };

  const userTypes = [
    {
      id: 'user',
      title: 'Community User',
      description: 'Project creation and management',
      icon: <UserIcon />,
      color: 'primary'
    },
    {
      id: 'admin',
      title: 'NCCR Admin',
      description: 'Final approval and oversight',
      icon: <AdminIcon />,
      color: 'secondary'
    }
  ];

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      display: 'flex',
      alignItems: 'center',
      position: 'relative',
      overflow: 'hidden',
      '&::before': {
        content: '""',
        position: 'absolute',
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        background: 'url("data:image/svg+xml,%3Csvg width="60" height="60" viewBox="0 0 60 60" xmlns="http://www.w3.org/2000/svg"%3E%3Cg fill="none" fill-rule="evenodd"%3E%3Cg fill="%23ffffff" fill-opacity="0.05"%3E%3Cpath d="M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z"/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")',
      },
    }}>
      <Container maxWidth="lg" sx={{ position: 'relative', zIndex: 1 }}>
        <Grid container spacing={4} alignItems="center">
          {/* Left side - Login Form */}
          <Grid item xs={12} md={6}>
            <Box
              sx={{
                background: 'rgba(255, 255, 255, 0.95)',
                backdropFilter: 'blur(20px)',
                borderRadius: 6,
                p: 4,
                boxShadow: '0 32px 64px rgba(0, 0, 0, 0.1)',
                border: '1px solid rgba(255, 255, 255, 0.2)',
                animation: 'slideInLeft 0.8s ease-out',
                '@keyframes slideInLeft': {
                  from: {
                    opacity: 0,
                    transform: 'translateX(-50px)',
                  },
                  to: {
                    opacity: 1,
                    transform: 'translateX(0)',
                  },
                },
              }}
            >
              <Box sx={{ textAlign: 'center', mb: 4 }}>
                <NatureIcon sx={{ fontSize: 60, color: '#1976d2', mb: 2 }} />
                <Typography variant="h4" component="h1" gutterBottom>
                  Blue Carbon MRV
                </Typography>
                <Typography variant="subtitle1" color="textSecondary">
                  Monitoring, Reporting & Verification System
                </Typography>
              </Box>

              <Alert severity="info" sx={{ mb: 3 }}>
                <Typography variant="subtitle2" gutterBottom>
                  Demo Login Options:
                </Typography>
                <Typography variant="body2">
                  ‚Ä¢ <strong>Community User:</strong> Create and manage blue carbon projects<br/>
                  ‚Ä¢ <strong>NCCR Admin:</strong> Final approval and system oversight
                </Typography>
              </Alert>

              {message && (
                <Alert severity={message.includes('successful') ? 'success' : 'error'} sx={{ mb: 3 }}>
                  {message}
                </Alert>
              )}

              {/* User Type Selection */}
              <Typography variant="h6" gutterBottom>
                Select Login Type
              </Typography>
              <Grid container spacing={2} sx={{ mb: 3 }}>
                {userTypes.map((type) => (
                  <Grid item xs={6} key={type.id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: userType === type.id ? 2 : 1,
                        borderColor: userType === type.id ? `${type.color}.main` : 'grey.300',
                        backgroundColor: userType === type.id ? `${type.color}.50` : 'background.paper',
                        '&:hover': {
                          borderColor: `${type.color}.main`,
                          backgroundColor: `${type.color}.50`,
                        },
                      }}
                      onClick={() => setUserType(type.id)}
                    >
                      <CardContent sx={{ textAlign: 'center', py: 2 }}>
                        <Box sx={{ color: `${type.color}.main`, mb: 1 }}>
                          {type.icon}
                        </Box>
                        <Typography variant="subtitle2" fontWeight="bold" fontSize="0.9rem">
                          {type.title}
                        </Typography>
                        <Typography variant="caption" color="textSecondary" fontSize="0.75rem">
                          {type.description}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>

              <Divider sx={{ my: 3 }} />

              {/* Login Form */}
              <form onSubmit={handleSubmit}>
                <TextField
                  fullWidth
                  name="email"
                  label="Email Address"
                  type="email"
                  value={formData.email}
                  onChange={handleChange}
                  margin="normal"
                  required
                  disabled={loading}
                />

                <TextField
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  value={formData.password}
                  onChange={handleChange}
                  margin="normal"
                  required
                  disabled={loading}
                />

                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  size="large"
                  disabled={loading}
                  startIcon={loading ? <CircularProgress size={20} /> : <LoginIcon />}
                  sx={{ mt: 3, mb: 2 }}
                >
                  {loading ? 'Logging in...' : 'Login'}
                </Button>
              </form>

              <Divider sx={{ my: 3 }}>
                <Chip label="Quick Demo Login" />
              </Divider>

              {/* Demo Login Buttons */}
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <Button
                    fullWidth
                    variant="outlined"
                    color="primary"
                    onClick={() => handleDemoLogin('user')}
                    disabled={loading}
                    startIcon={<UserIcon />}
                    size="small"
                  >
                    Demo User
                  </Button>
                </Grid>
                <Grid item xs={6}>
                  <Button
                    fullWidth
                    variant="outlined"
                    color="secondary"
                    onClick={() => handleDemoLogin('admin')}
                    disabled={loading}
                    startIcon={<AdminIcon />}
                    size="small"
                  >
                    Demo Admin
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </Grid>

          {/* Right side - Features */}
          <Grid item xs={12} md={6}>
            <Box 
              sx={{ 
                pl: { md: 4 },
                color: 'white',
                animation: 'slideInRight 0.8s ease-out',
                '@keyframes slideInRight': {
                  from: {
                    opacity: 0,
                    transform: 'translateX(50px)',
                  },
                  to: {
                    opacity: 1,
                    transform: 'translateX(0)',
                  },
                },
              }}
            >
              <Typography 
                variant="h2" 
                component="h2" 
                gutterBottom
                sx={{
                  fontWeight: 700,
                  background: 'linear-gradient(45deg, #ffffff 30%, #e8f4fd 90%)',
                  backgroundClip: 'text',
                  WebkitBackgroundClip: 'text',
                  WebkitTextFillColor: 'transparent',
                }}
              >
                üåä India's Blue Carbon Initiative
              </Typography>
              <Typography variant="h5" sx={{ mb: 4, opacity: 0.9 }}>
                Blockchain-powered MRV system for coastal ecosystem restoration
              </Typography>

              <Box sx={{ mt: 4 }}>
                <Typography variant="h5" sx={{ mb: 3, fontWeight: 600 }}>
                  üöÄ System Features
                </Typography>
                
                {[
                  { icon: 'üì±', title: 'Project Management', desc: 'Create and track blue carbon restoration projects' },
                  { icon: 'ü§ñ', title: 'AI Verification', desc: 'Automated verification using machine learning' },
                  { icon: '‚õìÔ∏è', title: 'Blockchain Integration', desc: 'Secure carbon credit tokenization on Polygon' },
                  { icon: 'üè™', title: 'Carbon Marketplace', desc: 'Trade verified credits with automated payments' }
                ].map((feature, index) => (
                  <Box 
                    key={index}
                    sx={{ 
                      mb: 3,
                      p: 3,
                      background: 'rgba(255, 255, 255, 0.1)',
                      borderRadius: 3,
                      backdropFilter: 'blur(10px)',
                      border: '1px solid rgba(255, 255, 255, 0.2)',
                      transition: 'transform 0.3s ease',
                      '&:hover': {
                        transform: 'translateX(10px)',
                      }
                    }}
                  >
                    <Typography variant="h6" sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 1 }}>
                      <Box sx={{ fontSize: '1.5rem' }}>{feature.icon}</Box>
                      {feature.title}
                    </Typography>
                    <Typography variant="body2" sx={{ opacity: 0.8, pl: 4 }}>
                      {feature.desc}
                    </Typography>
                  </Box>
                ))}
              </Box>

              <Box
                sx={{
                  mt: 4,
                  p: 3,
                  background: 'linear-gradient(45deg, rgba(67, 233, 123, 0.2) 0%, rgba(56, 249, 215, 0.2) 100%)',
                  borderRadius: 3,
                  border: '1px solid rgba(67, 233, 123, 0.3)',
                }}
              >
                <Typography variant="h6" sx={{ mb: 1 }}>
                  üåç Supporting UN SDG 14: Life Below Water
                </Typography>
                <Typography variant="body2" sx={{ opacity: 0.9 }}>
                  Contributing to India's climate goals through coastal ecosystem restoration
                </Typography>
              </Box>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Login;
