import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  TextField,
  Button,
  Typography,
  Alert,
  Card,
  CardContent,
  Grid,
  Tabs,
  Tab,
  CircularProgress,
  Divider,
} from '@mui/material';
import {
  Person as PersonIcon,
  AdminPanelSettings as AdminIcon,
  Login as LoginIcon,
  Nature as EcoIcon,
} from '@mui/icons-material';

const ProfessionalLogin = () => {
  const navigate = useNavigate();
  
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [userType, setUserType] = useState('user');
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleTabChange = (event, newValue) => {
    setUserType(newValue);
    setFormData({ email: '', password: '' });
    setMessage('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      await new Promise(resolve => setTimeout(resolve, 1000));

      if (formData.email && formData.password) {
        const userData = {
          email: formData.email,
          userType: userType,
          loginTime: new Date().toISOString(),
          name: userType === 'admin' ? 'NCCR Administrator' : 'Project Manager'
        };

        localStorage.setItem('userInfo', JSON.stringify(userData));
        localStorage.setItem('auth_token', `demo_token_${userType}_${Date.now()}`);

        setMessage(`Login successful! Welcome, ${userData.name}`);

        setTimeout(() => {
          if (userType === 'admin') {
            navigate('/admin/dashboard');
          } else {
            navigate('/dashboard');
          }
        }, 1000);
      } else {
        setMessage('Please enter email and password');
      }
    } catch (error) {
      console.error('Login error:', error);
      setMessage('Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = (demoUserType) => {
    if (demoUserType === 'admin') {
      setFormData({
        email: 'admin@nccr.gov.in',
        password: 'admin123'
      });
    } else {
      setFormData({
        email: 'user@community.org',
        password: 'user123'
      });
    }
    setUserType(demoUserType);
  };

  return (
    <Box sx={{ minHeight: '100vh', display: 'flex' }}>
      {/* Left Panel - Branding */}
      <Box
        sx={{
          flex: 1,
          backgroundColor: '#0C4A6E',
          color: 'white',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          px: 6,
          py: 8,
        }}
      >
        <Box sx={{ maxWidth: 480, mx: 'auto' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
            <EcoIcon sx={{ fontSize: 48, mr: 2 }} />
            <Typography variant="h3" component="h1" fontWeight={600}>
              Blue Carbon MRV
            </Typography>
          </Box>
          
          <Typography variant="h5" sx={{ mb: 3, opacity: 0.9, fontWeight: 400 }}>
            Monitoring, Reporting & Verification System for Coastal Ecosystems
          </Typography>
          
          <Typography variant="body1" sx={{ mb: 4, opacity: 0.8, lineHeight: 1.7 }}>
            Professional platform for blue carbon project management, blockchain-based 
            verification, and carbon credit tokenization. Supporting India's climate goals 
            through coastal ecosystem restoration.
          </Typography>

          <Box sx={{ mb: 4 }}>
            <Typography variant="h6" sx={{ mb: 2, fontWeight: 500 }}>
              Key Features
            </Typography>
            {[
              'AI-Powered Project Verification',
              'Blockchain Carbon Credit Registry',
              'Real-time Monitoring Dashboard',
              'Automated Payment Distribution'
            ].map((feature, index) => (
              <Box key={index} sx={{ display: 'flex', alignItems: 'center', mb: 1.5 }}>
                <Box
                  sx={{
                    width: 6,
                    height: 6,
                    borderRadius: '50%',
                    backgroundColor: '#059669',
                    mr: 2,
                  }}
                />
                <Typography variant="body2" sx={{ opacity: 0.9 }}>
                  {feature}
                </Typography>
              </Box>
            ))}
          </Box>

          <Box
            sx={{
              p: 3,
              backgroundColor: 'rgba(5, 150, 105, 0.1)',
              borderRadius: 2,
              border: '1px solid rgba(5, 150, 105, 0.2)',
            }}
          >
            <Typography variant="subtitle2" sx={{ fontWeight: 600, mb: 1 }}>
              Supporting UN SDG 14: Life Below Water
            </Typography>
            <Typography variant="body2" sx={{ opacity: 0.8 }}>
              Contributing to global climate action through sustainable blue carbon initiatives
            </Typography>
          </Box>
        </Box>
      </Box>

      {/* Right Panel - Login Form */}
      <Box
        sx={{
          flex: 1,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: '#F8FAFC',
          px: 4,
        }}
      >
        <Container maxWidth="sm">
          <Card
            sx={{
              p: 4,
              boxShadow: '0 4px 6px rgba(0, 0, 0, 0.05)',
              border: '1px solid #E2E8F0',
            }}
          >
            <CardContent sx={{ p: 0 }}>
              <Typography variant="h4" align="center" sx={{ mb: 1, fontWeight: 600 }}>
                Welcome Back
              </Typography>
              <Typography
                variant="body2"
                align="center"
                color="text.secondary"
                sx={{ mb: 4 }}
              >
                Sign in to your account to continue
              </Typography>

              {message && (
                <Alert
                  severity={message.includes('successful') ? 'success' : 'error'}
                  sx={{ mb: 3 }}
                >
                  {message}
                </Alert>
              )}

              {/* Role Selection Tabs */}
              <Tabs
                value={userType}
                onChange={handleTabChange}
                centered
                sx={{
                  mb: 3,
                  '& .MuiTab-root': {
                    textTransform: 'none',
                    fontWeight: 500,
                    minHeight: 48,
                  },
                }}
              >
                <Tab
                  label="Community User"
                  value="user"
                  icon={<PersonIcon />}
                  iconPosition="start"
                />
                <Tab
                  label="NCCR Admin"
                  value="admin"
                  icon={<AdminIcon />}
                  iconPosition="start"
                />
              </Tabs>

              {/* Login Form */}
              <form onSubmit={handleSubmit}>
                <TextField
                  fullWidth
                  name="email"
                  label="Email Address"
                  type="email"
                  value={formData.email}
                  onChange={handleChange}
                  margin="normal"
                  required
                  disabled={loading}
                  sx={{ mb: 2 }}
                />

                <TextField
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  value={formData.password}
                  onChange={handleChange}
                  margin="normal"
                  required
                  disabled={loading}
                  sx={{ mb: 3 }}
                />

                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  size="large"
                  disabled={loading}
                  startIcon={loading ? <CircularProgress size={20} /> : <LoginIcon />}
                  sx={{
                    mb: 3,
                    py: 1.5,
                    fontSize: '1rem',
                    fontWeight: 500,
                  }}
                >
                  {loading ? 'Signing in...' : 'Sign In'}
                </Button>
              </form>

              <Divider sx={{ my: 3 }}>
                <Typography variant="caption" color="text.secondary">
                  Quick Demo Access
                </Typography>
              </Divider>

              {/* Demo Login Buttons */}
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <Button
                    fullWidth
                    variant="outlined"
                    onClick={() => handleDemoLogin('user')}
                    disabled={loading}
                    startIcon={<PersonIcon />}
                    sx={{
                      py: 1.5,
                      borderColor: '#E2E8F0',
                      color: '#475569',
                      '&:hover': {
                        borderColor: '#0C4A6E',
                        color: '#0C4A6E',
                      },
                    }}
                  >
                    Demo User
                  </Button>
                </Grid>
                <Grid item xs={6}>
                  <Button
                    fullWidth
                    variant="outlined"
                    onClick={() => handleDemoLogin('admin')}
                    disabled={loading}
                    startIcon={<AdminIcon />}
                    sx={{
                      py: 1.5,
                      borderColor: '#E2E8F0',
                      color: '#475569',
                      '&:hover': {
                        borderColor: '#059669',
                        color: '#059669',
                      },
                    }}
                  >
                    Demo Admin
                  </Button>
                </Grid>
              </Grid>
            </CardContent>
          </Card>
        </Container>
      </Box>
    </Box>
  );
};

export default ProfessionalLogin;
