import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Box,
  Container,
  Paper,
  TextField,
  Button,
  Typography,
  Alert,
  Card,
  CardContent,
  Grid,
  Divider,
  Chip
} from '@mui/material';
import {
  Nature as NatureIcon,
  Person as UserIcon,
  AdminPanelSettings as AdminIcon
} from '@mui/icons-material';
import { useAuth } from '../contexts/AuthContext';
import { useSnackbar } from 'notistack';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  const { enqueueSnackbar } = useSnackbar();

  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const result = await login(formData.email, formData.password);

      if (result.success) {
        enqueueSnackbar(`Welcome, ${result.user.name}!`, { variant: 'success' });

        // Redirect based on role
        if (result.user.role === 'admin') {
          navigate('/admin/dashboard');
        } else {
          navigate('/user/dashboard');
        }
      }
    } catch (err) {
      setError(err.message);
      enqueueSnackbar('Login failed', { variant: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = (userType) => {
    if (userType === 'admin') {
      setFormData({
        email: 'admin@nccr.gov.in',
        password: 'admin123'
      });
    } else {
      setFormData({
        email: 'user@demo.com',
        password: 'user123'
      });
    }
  };

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e3f2fd 0%, #bbdefb 100%)',
      display: 'flex',
      alignItems: 'center'
    }}>
      <Container maxWidth="lg">
        <Grid container spacing={4} alignItems="center">
          {/* Left side - Login Form */}
          <Grid item xs={12} md={6}>
            <Paper elevation={8} sx={{ p: 4, borderRadius: 3 }}>
              <Box sx={{ textAlign: 'center', mb: 4 }}>
                <NatureIcon sx={{ fontSize: 60, color: '#1976d2', mb: 2 }} />
                <Typography variant="h4" component="h1" gutterBottom>
                  Blue Carbon MRV
                </Typography>
                <Typography variant="subtitle1" color="textSecondary">
                  Monitoring, Reporting & Verification System
                </Typography>
              </Box>

              {error && (
                <Alert severity="error" sx={{ mb: 3 }}>
                  {error}
                </Alert>
              )}

              <form onSubmit={handleSubmit}>
                <TextField
                  fullWidth
                  label="Email Address"
                  name="email"
                  type="email"
                  value={formData.email}
                  onChange={handleChange}
                  required
                  sx={{ mb: 3 }}
                />

                <TextField
                  fullWidth
                  label="Password"
                  name="password"
                  type="password"
                  value={formData.password}
                  onChange={handleChange}
                  required
                  sx={{ mb: 3 }}
                />

                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  size="large"
                  disabled={loading}
                  sx={{ mb: 3, py: 1.5 }}
                >
                  {loading ? 'Signing In...' : 'Sign In'}
                </Button>
              </form>

              <Divider sx={{ my: 3 }}>
                <Chip label="Demo Access" />
              </Divider>

              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <Button
                    fullWidth
                    variant="outlined"
                    startIcon={<UserIcon />}
                    onClick={() => handleDemoLogin('user')}
                    sx={{ py: 1.5 }}
                  >
                    User Demo
                  </Button>
                </Grid>
                <Grid item xs={6}>
                  <Button
                    fullWidth
                    variant="outlined"
                    startIcon={<AdminIcon />}
                    onClick={() => handleDemoLogin('admin')}
                    sx={{ py: 1.5 }}
                  >
                    Admin Demo
                  </Button>
                </Grid>
              </Grid>

              <Box sx={{ mt: 3, textAlign: 'center' }}>
                <Button 
                  variant="text" 
                  onClick={() => navigate('/')}
                  sx={{ textDecoration: 'underline' }}
                >
                  ‚Üê Back to Home
                </Button>
              </Box>
            </Paper>
          </Grid>

          {/* Right side - Information */}
          <Grid item xs={12} md={6}>
            <Box sx={{ pl: { md: 4 } }}>
              <Typography variant="h3" component="h2" gutterBottom color="primary">
                Access Your Portal
              </Typography>
              <Typography variant="h6" paragraph color="textSecondary">
                Choose your access level to get started with the Blue Carbon MRV System
              </Typography>

              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <Card sx={{ border: '2px solid #4caf50' }}>
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <UserIcon sx={{ fontSize: 40, color: '#4caf50', mr: 2 }} />
                        <Box>
                          <Typography variant="h6" color="#4caf50">
                            Community Users
                          </Typography>
                          <Typography variant="body2" color="textSecondary">
                            Submit and track restoration projects
                          </Typography>
                        </Box>
                      </Box>
                      <Typography variant="body2">
                        <strong>Demo Credentials:</strong><br />
                        Email: user@demo.com<br />
                        Password: user123
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>

                <Grid item xs={12}>
                  <Card sx={{ border: '2px solid #ff9800' }}>
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <AdminIcon sx={{ fontSize: 40, color: '#ff9800', mr: 2 }} />
                        <Box>
                          <Typography variant="h6" color="#ff9800">
                            NCCR Administrators
                          </Typography>
                          <Typography variant="body2" color="textSecondary">
                            Review and manage the entire system
                          </Typography>
                        </Box>
                      </Box>
                      <Typography variant="body2">
                        <strong>Demo Credentials:</strong><br />
                        Email: admin@nccr.gov.in<br />
                        Password: admin123
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              </Grid>

              <Box sx={{ mt: 4, p: 3, background: 'rgba(255,255,255,0.8)', borderRadius: 2 }}>
                <Typography variant="h6" gutterBottom>
                  üîí Security Notice
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  This is a demonstration system. In production, proper authentication 
                  and authorization would be implemented with secure credentials.
                </Typography>
              </Box>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Login;
