} from '@mui/material';
import { alpha, useTheme } from '@mui/material/styles';
import {
  Nature as NatureIcon,
  Person as UserIcon,
  AdminPanelSettings as AdminIcon,
  Login as LoginIcon
} from '@mui/icons-material';

const Login = () => {
  const navigate = useNavigate();
  const theme = useTheme();
  
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [userType, setUserType] = useState('user');
  const [message, setMessage] = useState('');

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');

    try {
      // Simple user/admin login only
      await new Promise(resolve => setTimeout(resolve, 1000));

      if (formData.email && formData.password) {
        const userData = {
          email: formData.email,
          userType: userType,
          loginTime: new Date().toISOString(),
          name: userType === 'admin' ? 'NCCR Administrator' : 'Project Manager'
        };

        localStorage.setItem('userInfo', JSON.stringify(userData));
        localStorage.setItem('auth_token', `demo_token_${userType}_${Date.now()}`);

        setMessage(`Login successful! Welcome, ${userData.name}`);

        setTimeout(() => {
          if (userType === 'admin') {
            navigate('/admin/dashboard');
          } else {
            navigate('/dashboard');
          }
        }, 1000);
      } else {
        setMessage('Please enter email and password');
      }
    } catch (error) {
      console.error('Login error:', error);
      setMessage('Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDemoLogin = (demoUserType) => {
    setUserType(demoUserType);
    if (demoUserType === 'admin') {
      setFormData({
        email: 'admin@nccr.gov.in',
        password: 'admin123'
      });
    } else {
      setFormData({
        email: 'user@community.org',
        password: 'user123'
      });
    }
  };

  const userTypes = [
    {
      id: 'user',
      title: 'Community User',
      description: 'Project creation and management',
      icon: <UserIcon />,
      color: 'primary'
    },
    {
      id: 'admin',
      title: 'NCCR Admin',
      description: 'Final approval and oversight',
      icon: <AdminIcon />,
      color: 'secondary'
    }
  ];

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: `linear-gradient(135deg, ${theme.palette.background.sidebar} 0%, ${alpha(theme.palette.primary.main, 0.8)} 100%)`,
      display: 'flex',
      alignItems: 'center',
      overflow: 'hidden', // Remove scroll bar
    }}>
      <Container maxWidth="lg">
        <Grid container spacing={4} alignItems="center">
          {/* Left side - Login Form */}
          <Grid item xs={12} md={6}>
            <Paper 
              elevation={8} 
              sx={{ 
                p: 4, 
                borderRadius: 3,
                backgroundColor: alpha(theme.palette.background.paper, 0.95),
                backdropFilter: 'blur(20px)',
                border: `1px solid ${alpha(theme.palette.primary.main, 0.1)}`,
              }}
            >
              <Box sx={{ textAlign: 'center', mb: 4 }}>
                <Box
                  sx={{
                    width: 80,
                    height: 80,
                    borderRadius: '50%',
                    background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.secondary.main} 100%)`,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontSize: '2rem',
                    mx: 'auto',
                    mb: 2,
                    boxShadow: `0 8px 32px ${alpha(theme.palette.primary.main, 0.3)}`,
                  }}
                >
                  üåä
                </Box>
                <Typography 
                  variant="h4" 
                  component="h1" 
                  gutterBottom
                  sx={{
                    fontWeight: 600,
                    background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.secondary.main} 100%)`,
                    backgroundClip: 'text',
                    WebkitBackgroundClip: 'text',
                    color: 'transparent',
                  }}
                >
                  Blue Carbon MRV
                </Typography>
                <Typography variant="subtitle1" color="textSecondary">
                  Monitoring, Reporting & Verification System
                </Typography>
              </Box>

              <Alert 
                severity="info" 
                sx={{ 
                  mb: 3,
                  backgroundColor: alpha(theme.palette.info.main, 0.1),
                  border: `1px solid ${alpha(theme.palette.info.main, 0.2)}`,
                  borderRadius: 2,
                }}
              >
                <Typography variant="subtitle2" gutterBottom fontWeight={600}>
                  Demo Login Options:
                </Typography>
                <Typography variant="body2">
                  ‚Ä¢ <strong>Community User:</strong> Create and manage blue carbon projects<br/>
                  ‚Ä¢ <strong>NCCR Admin:</strong> Final approval and system oversight
                </Typography>
              </Alert>

              {message && (
                <Alert severity={message.includes('successful') ? 'success' : 'error'} sx={{ mb: 3 }}>
                  {message}
                </Alert>
              )}

              {/* User Type Selection */}
              <Typography variant="h6" gutterBottom fontWeight={600}>
                Select Login Type
              </Typography>
              <Grid container spacing={2} sx={{ mb: 3 }}>
                {userTypes.map((type) => (
                  <Grid item xs={6} key={type.id}>
                    <Card
                      sx={{
                        cursor: 'pointer',
                        border: userType === type.id ? 2 : 1,
                        borderColor: userType === type.id ? `${type.color}.main` : alpha(theme.palette.divider, 0.3),
                        backgroundColor: userType === type.id 
                          ? alpha(theme.palette[type.color].main, 0.1) 
                          : 'background.paper',
                        transition: 'all 0.2s ease-in-out',
                        '&:hover': {
                          borderColor: `${type.color}.main`,
                          backgroundColor: alpha(theme.palette[type.color].main, 0.05),
                          transform: 'translateY(-2px)',
                          boxShadow: `0 8px 16px ${alpha(theme.palette[type.color].main, 0.2)}`,
                        },
                      }}
                      onClick={() => setUserType(type.id)}
                    >
                      <CardContent sx={{ textAlign: 'center', py: 2 }}>
                        <Box 
                          sx={{ 
                            color: `${type.color}.main`, 
                            mb: 1,
                            fontSize: '1.5rem',
                          }}
                        >
                          {type.icon}
                        </Box>
                        <Typography variant="subtitle2" fontWeight="bold" fontSize="0.9rem">
                          {type.title}
                        </Typography>
                        <Typography variant="caption" color="textSecondary" fontSize="0.75rem">
                          {type.description}
                        </Typography>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>

              <Divider sx={{ my: 3 }} />

              {/* Login Form */}
              <form onSubmit={handleSubmit}>
                <TextField
                  fullWidth
                  name="email"
                  label="Email Address"
                  type="email"
                  value={formData.email}
                  onChange={handleChange}
                  margin="normal"
                  required
                  disabled={loading}
                />

                <TextField
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  value={formData.password}
                  onChange={handleChange}
                  margin="normal"
                  required
                  disabled={loading}
                />

                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  size="large"
                  disabled={loading}
                  startIcon={loading ? <CircularProgress size={20} color="inherit" /> : <LoginIcon />}
                  sx={{ 
                    mt: 3, 
                    mb: 2,
                    background: `linear-gradient(135deg, ${theme.palette.primary.main} 0%, ${theme.palette.secondary.main} 100%)`,
                    boxShadow: `0 4px 16px ${alpha(theme.palette.primary.main, 0.3)}`,
                    '&:hover': {
                      background: `linear-gradient(135deg, ${theme.palette.primary.dark} 0%, ${theme.palette.secondary.dark} 100%)`,
                      boxShadow: `0 6px 20px ${alpha(theme.palette.primary.main, 0.4)}`,
                    },
                  }}
                >
                  {loading ? 'Logging in...' : 'Login'}
                </Button>
              </form>

              <Divider sx={{ my: 3 }}>
                <Chip 
                  label="Quick Demo Login" 
                  sx={{
                    backgroundColor: alpha(theme.palette.primary.main, 0.1),
                    color: theme.palette.primary.main,
                    fontWeight: 600,
                  }}
                />
              </Divider>

              {/* Demo Login Buttons */}
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <Button
                    fullWidth
                    variant="outlined"
                    color="primary"
                    onClick={() => handleDemoLogin('user')}
                    disabled={loading}
                    startIcon={<UserIcon />}
                    size="small"
                    sx={{
                      borderColor: theme.palette.primary.main,
                      '&:hover': {
                        backgroundColor: alpha(theme.palette.primary.main, 0.1),
                        borderColor: theme.palette.primary.dark,
                      },
                    }}
                  >
                    Demo User
                  </Button>
                </Grid>
                <Grid item xs={6}>
                  <Button
                    fullWidth
                    variant="outlined"
                    color="secondary"
                    onClick={() => handleDemoLogin('admin')}
                    disabled={loading}
                    startIcon={<AdminIcon />}
                    size="small"
                    sx={{
                      borderColor: theme.palette.secondary.main,
                      '&:hover': {
                        backgroundColor: alpha(theme.palette.secondary.main, 0.1),
                        borderColor: theme.palette.secondary.dark,
                      },
                    }}
                  >
                    Demo Admin
                  </Button>
                </Grid>
              </Grid>
            </Paper>
          </Grid>

          {/* Right side - Features */}
          <Grid item xs={12} md={6}>
            <Box sx={{ pl: { md: 4 } }}>
              <Typography 
                variant="h3" 
                component="h2" 
                gutterBottom
                sx={{
                  fontWeight: 700,
                  color: theme.palette.common.white,
                  textShadow: '0 2px 4px rgba(0,0,0,0.3)',
                }}
              >
                üåä India's Blue Carbon Initiative
              </Typography>
              <Typography 
                variant="h6" 
                gutterBottom
                sx={{
                  color: alpha(theme.palette.common.white, 0.9),
                  fontWeight: 500,
                }}
              >
                Blockchain-powered MRV system for coastal ecosystem restoration
              </Typography>

              <Box sx={{ mt: 4 }}>
                <Typography 
                  variant="h6" 
                  gutterBottom
                  sx={{
                    color: theme.palette.common.white,
                    fontWeight: 600,
                  }}
                >
                  üöÄ System Features
                </Typography>
                
                <Box sx={{ mb: 2 }}>
                  <Typography 
                    variant="subtitle1" 
                    fontWeight="bold"
                    sx={{ color: theme.palette.common.white }}
                  >
                    üì± Project Management
                  </Typography>
                  <Typography 
                    variant="body2" 
                    sx={{ color: alpha(theme.palette.common.white, 0.8) }}
                  >
                    Create and track blue carbon restoration projects with comprehensive data collection
                  </Typography>
                </Box>

                <Box sx={{ mb: 2 }}>
                  <Typography 
                    variant="subtitle1" 
                    fontWeight="bold"
                    sx={{ color: theme.palette.common.white }}
                  >
                    ü§ñ AI Verification
                  </Typography>
                  <Typography 
                    variant="body2" 
                    sx={{ color: alpha(theme.palette.common.white, 0.8) }}
                  >
                    Automated project verification using machine learning and fraud detection
                  </Typography>
                </Box>

                <Box sx={{ mb: 2 }}>
                  <Typography 
                    variant="subtitle1" 
                    fontWeight="bold"
                    sx={{ color: theme.palette.common.white }}
                  >
                    ‚õìÔ∏è Blockchain Integration
                  </Typography>
                  <Typography 
                    variant="body2" 
                    sx={{ color: alpha(theme.palette.common.white, 0.8) }}
                  >
                    Secure carbon credit tokenization on Polygon network
                  </Typography>
                </Box>

                <Box sx={{ mb: 2 }}>
                  <Typography 
                    variant="subtitle1" 
                    fontWeight="bold"
                    sx={{ color: theme.palette.common.white }}
                  >
                    üè™ Carbon Marketplace
                  </Typography>
                  <Typography 
                    variant="body2" 
                    sx={{ color: alpha(theme.palette.common.white, 0.8) }}
                  >
                    Trade verified carbon credits with automated payment distribution
                  </Typography>
                </Box>
              </Box>

              <Alert 
                severity="success" 
                sx={{ 
                  mt: 3,
                  backgroundColor: alpha(theme.palette.success.main, 0.15),
                  color: theme.palette.common.white,
                  border: `1px solid ${alpha(theme.palette.success.main, 0.3)}`,
                  '& .MuiAlert-icon': {
                    color: theme.palette.success.light,
                  }
                }}
              >
                <Typography variant="subtitle2" fontWeight={600}>
                  üåç Supporting UN SDG 14: Life Below Water
                </Typography>
                <Typography variant="body2">
                  Contributing to India's climate goals through coastal ecosystem restoration
                </Typography>
              </Alert>
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Login;
