import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Typography,
  Box,
  Grid,
  Card,
  CardContent,
  Button,
  Alert,
  LinearProgress,
  Chip,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  Divider,
  Paper,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from '@mui/material';
import {
  Add as AddIcon,
  Nature as EcoIcon,
  CheckCircle as CheckCircleIcon,
  Schedule as ScheduleIcon,
  PhotoCamera as PhotoIcon,
  LocationOn as LocationIcon,
  Store as MarketIcon,
  AccountBalance as ProjectIcon,
  Verified as VerifiedIcon,
  Payment as PaymentIcon,
  TrendingUp as TrendingUpIcon,
  TrendingDown as TrendingDownIcon,
  Visibility as ViewIcon,
} from '@mui/icons-material';

const ProfessionalDashboard = () => {
  const navigate = useNavigate();
  const [userInfo, setUserInfo] = useState(null);
  const [dashboardData, setDashboardData] = useState({
    projects: [],
    stats: {
      totalProjects: 0,
      pendingReview: 0,
      approved: 0,
      carbonCredits: 0
    }
  });

  useEffect(() => {
    const storedUserInfo = localStorage.getItem('userInfo');
    if (storedUserInfo) {
      setUserInfo(JSON.parse(storedUserInfo));
    }
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await fetch('https://python-backend-dqu4.onrender.com/api/projects');
      const data = await response.json();
      
      const projectsWithStatus = await Promise.all(
        (data.projects || []).map(async (project) => {
          try {
            const statusResponse = await fetch(`https://python-backend-dqu4.onrender.com/api/projects/${project.id}/verification-status`);
            const statusData = await statusResponse.json();
            return {
              ...project,
              verification_status: statusData.success ? statusData.verification_status : null
            };
          } catch (error) {
            console.warn(`Could not load status for project ${project.id}`);
            return project;
          }
        })
      );
      
      setDashboardData({
        projects: projectsWithStatus,
        stats: {
          totalProjects: projectsWithStatus.length,
          pendingReview: projectsWithStatus.filter(p => p.status === 'pending_verification' || p.status === 'requires_review').length,
          approved: projectsWithStatus.filter(p => p.status === 'approved').length,
          carbonCredits: projectsWithStatus.reduce((sum, p) => sum + (p.carbon_credits || 0), 0),
          thirdPartyVerified: projectsWithStatus.filter(p => p.verification_status?.verification_stages?.third_party_verification?.completed).length,
          blockchainRegistered: projectsWithStatus.filter(p => p.verification_status?.verification_stages?.blockchain_registration?.completed).length
        }
      });
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    }
  };

  const quickActions = [
    {
      title: 'Create New Project',
      description: 'Start a new blue carbon restoration project',
      icon: <AddIcon />,
      action: () => navigate('/projects/create'),
      color: '#0C4A6E'
    },
    {
      title: 'View Marketplace',
      description: 'Trade verified carbon credits',
      icon: <MarketIcon />,
      action: () => navigate('/marketplace'),
      color: '#059669'
    },
    {
      title: 'Carbon Credits',
      description: 'Manage tokenized carbon credits',
      icon: <VerifiedIcon />,
      action: () => navigate('/carbon-credits'),
      color: '#D97706'
    },
    {
      title: 'View Reports',
      description: 'Access analytics and insights',
      icon: <PhotoIcon />,
      action: () => navigate('/reports'),
      color: '#0284C7'
    }
  ];

  const recentActivities = [
    {
      title: 'Project Verification Completed',
      description: 'Mangrove restoration project approved',
      time: '2 hours ago',
      type: 'success'
    },
    {
      title: 'New Carbon Credits Issued',
      description: '500 credits tokenized on blockchain',
      time: '5 hours ago',
      type: 'info'
    },
    {
      title: 'Payment Distributed',
      description: 'Community payment of â‚¹50,000 processed',
      time: '1 day ago',
      type: 'success'
    },
    {
      title: 'AI Verification Started',
      description: 'Automated analysis initiated for Project #127',
      time: '2 days ago',
      type: 'info'
    }
  ];

  const StatCard = ({ title, value, icon, trend, trendValue }) => (
    <Card>
      <CardContent>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
          <Box>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              {title}
            </Typography>
            <Typography variant="h4" sx={{ fontWeight: 600, mb: 1 }}>
              {value}
            </Typography>
            {trend && (
              <Box sx={{ display: 'flex', alignItems: 'center' }}>
                {trend === 'up' ? (
                  <TrendingUpIcon sx={{ fontSize: 16, color: '#059669', mr: 0.5 }} />
                ) : (
                  <TrendingDownIcon sx={{ fontSize: 16, color: '#DC2626', mr: 0.5 }} />
                )}
                <Typography variant="caption" color={trend === 'up' ? '#059669' : '#DC2626'}>
                  {trendValue}
                </Typography>
              </Box>
            )}
          </Box>
          <Box
            sx={{
              p: 1.5,
              backgroundColor: '#F1F5F9',
              borderRadius: 2,
              color: '#0C4A6E',
            }}
          >
            {icon}
          </Box>
        </Box>
      </CardContent>
    </Card>
  );

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Box sx={{ mb: 4 }}>
        <Typography variant="h4" sx={{ fontWeight: 600, mb: 1 }}>
          Dashboard
        </Typography>
        <Typography variant="body1" color="text.secondary">
          Welcome back, {userInfo?.email || 'User'}. Here's what's happening with your projects.
        </Typography>
      </Box>

      {/* System Status */}
      <Alert severity="info" sx={{ mb: 4 }}>
        All systems operational. Backend connected, AI verification active, blockchain registry online.
      </Alert>

      {/* Stats Grid */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Total Projects"
            value={dashboardData.stats.totalProjects}
            icon={<ProjectIcon />}
            trend="up"
            trendValue="+12% this month"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Pending Review"
            value={dashboardData.stats.pendingReview}
            icon={<ScheduleIcon />}
            trend="down"
            trendValue="-5% this week"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Approved Projects"
            value={dashboardData.stats.approved}
            icon={<CheckCircleIcon />}
            trend="up"
            trendValue="+8% this month"
          />
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <StatCard
            title="Carbon Credits"
            value={dashboardData.stats.carbonCredits.toLocaleString()}
            icon={<EcoIcon />}
            trend="up"
            trendValue="+15% this month"
          />
        </Grid>
      </Grid>

      <Grid container spacing={3}>
        {/* Quick Actions */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ p: 3, mb: 3 }}>
            <Typography variant="h6" sx={{ mb: 3, fontWeight: 600 }}>
              Quick Actions
            </Typography>
            <Grid container spacing={2}>
              {quickActions.map((action, index) => (
                <Grid item xs={12} sm={6} key={index}>
                  <Card
                    sx={{
                      cursor: 'pointer',
                      transition: 'all 0.2s ease',
                      '&:hover': {
                        transform: 'translateY(-2px)',
                        boxShadow: '0 4px 12px rgba(0, 0, 0, 0.1)',
                      },
                    }}
                    onClick={action.action}
                  >
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                        <Box
                          sx={{
                            p: 1,
                            backgroundColor: `${action.color}15`,
                            borderRadius: 2,
                            color: action.color,
                            mr: 2,
                          }}
                        >
                          {action.icon}
                        </Box>
                        <Typography variant="h6" sx={{ fontWeight: 600 }}>
                          {action.title}
                        </Typography>
                      </Box>
                      <Typography variant="body2" color="text.secondary">
                        {action.description}
                      </Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </Paper>
        </Grid>

        {/* Recent Activity */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ p: 3 }}>
            <Typography variant="h6" sx={{ mb: 2, fontWeight: 600 }}>
              Recent Activity
            </Typography>
            <List>
              {recentActivities.map((activity, index) => (
                <React.Fragment key={index}>
                  <ListItem sx={{ px: 0, py: 1.5 }}>
                    <ListItemIcon>
                      <Box
                        sx={{
                          width: 8,
                          height: 8,
                          borderRadius: '50%',
                          backgroundColor: activity.type === 'success' ? '#059669' : '#0284C7',
                        }}
                      />
                    </ListItemIcon>
                    <ListItemText
                      primary={
                        <Typography variant="subtitle2" sx={{ fontWeight: 500 }}>
                          {activity.title}
                        </Typography>
                      }
                      secondary={
                        <>
                          <Typography variant="body2" color="text.secondary" sx={{ mb: 0.5 }}>
                            {activity.description}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {activity.time}
                          </Typography>
                        </>
                      }
                    />
                  </ListItem>
                  {index < recentActivities.length - 1 && <Divider />}
                </React.Fragment>
              ))}
            </List>
          </Paper>
        </Grid>

        {/* Recent Projects Table */}
        <Grid item xs={12}>
          <Paper sx={{ p: 3 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h6" sx={{ fontWeight: 600 }}>
                Recent Projects
              </Typography>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => navigate('/projects/create')}
              >
                Create New Project
              </Button>
            </Box>
            
            {dashboardData.projects.length === 0 ? (
              <Alert severity="info">
                No projects found. Create your first blue carbon restoration project to get started!
              </Alert>
            ) : (
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell sx={{ fontWeight: 600 }}>Project Name</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Status</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Ecosystem</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Area (ha)</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Progress</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Actions</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {dashboardData.projects.slice(0, 5).map((project) => {
                      const verificationStatus = project.verification_status;
                      const stages = verificationStatus?.verification_stages || {};
                      
                      let progress = 0;
                      if (stages.ai_verification?.completed) progress += 25;
                      if (stages.third_party_verification?.completed) progress += 25;
                      if (stages.admin_review?.completed) progress += 25;
                      if (stages.blockchain_registration?.completed) progress += 25;
                      
                      return (
                        <TableRow key={project.id}>
                          <TableCell>
                            <Typography variant="subtitle2" sx={{ fontWeight: 500 }}>
                              {project.project_name || 'Unnamed Project'}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            <Chip
                              label={project.status?.replace('_', ' ').toUpperCase()}
                              size="small"
                              color={project.status === 'approved' ? 'success' : 'warning'}
                              variant="outlined"
                            />
                          </TableCell>
                          <TableCell>
                            <Typography variant="body2">
                              {project.ecosystem_type}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            <Typography variant="body2">
                              {project.area_hectares}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                              <LinearProgress
                                variant="determinate"
                                value={progress}
                                sx={{ flexGrow: 1, height: 6 }}
                              />
                              <Typography variant="caption" color="text.secondary">
                                {progress}%
                              </Typography>
                            </Box>
                          </TableCell>
                          <TableCell>
                            <IconButton size="small" color="primary">
                              <ViewIcon />
                            </IconButton>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </TableContainer>
            )}
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ProfessionalDashboard;
