import React, { useState, useEffect } from 'react';
import {
  Box,
  Grid,
  Card,
  CardContent,
  Typography,
  useTheme,
  alpha,
  LinearProgress,
  Avatar,
  Chip,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
} from '@mui/material';
import {
  TrendingUp,
  TrendingDown,
  Eco as EcoIcon,
  VerifiedUser as VerificationIcon,
  Assessment as ReportsIcon,
  AccountBalance as MarketplaceIcon,
  Assignment as ProjectIcon,
  People as PeopleIcon,
  AttachMoney as MoneyIcon,
  Timeline as TimelineIcon,
  MoreVert as MoreIcon,
  CheckCircle,
  Schedule,
  Warning,
  Notifications,
} from '@mui/icons-material';
import { Line, Doughnut } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  ArcElement
);

const SIDEBAR_WIDTH = 280;

// Stat Card Component
const StatCard = ({ title, value, change, trend, icon, gradient, description }) => {
  const theme = useTheme();
  const isPositive = trend === 'up';

  return (
    <Card
      sx={{
        height: '100%',
        background: `linear-gradient(135deg, ${gradient})`,
        position: 'relative',
        overflow: 'hidden',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          right: 0,
          width: 100,
          height: 100,
          background: 'rgba(255, 255, 255, 0.1)',
          borderRadius: '50%',
          transform: 'translate(30%, -30%)',
        },
      }}
    >
      <CardContent sx={{ p: 3, position: 'relative', zIndex: 1 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
          <Avatar
            sx={{
              backgroundColor: 'rgba(255, 255, 255, 0.2)',
              color: 'white',
              width: 50,
              height: 50,
            }}
          >
            {icon}
          </Avatar>
          <IconButton
            size="small"
            sx={{ color: 'rgba(255, 255, 255, 0.7)' }}
          >
            <MoreIcon />
          </IconButton>
        </Box>
        
        <Typography
          variant="h3"
          sx={{
            fontWeight: 700,
            color: 'white',
            mb: 0.5,
            fontSize: '2.25rem',
          }}
        >
          {value}
        </Typography>
        
        <Typography
          variant="body1"
          sx={{
            color: 'rgba(255, 255, 255, 0.9)',
            fontWeight: 500,
            mb: 2,
          }}
        >
          {title}
        </Typography>

        <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            {isPositive ? (
              <TrendingUp sx={{ color: '#10B981', fontSize: 18, mr: 0.5 }} />
            ) : (
              <TrendingDown sx={{ color: '#EF4444', fontSize: 18, mr: 0.5 }} />
            )}
            <Typography
              variant="body2"
              sx={{
                color: isPositive ? '#10B981' : '#EF4444',
                fontWeight: 600,
                fontSize: '0.875rem',
              }}
            >
              {change}
            </Typography>
          </Box>
          
          <Typography
            variant="caption"
            sx={{
              color: 'rgba(255, 255, 255, 0.7)',
              fontSize: '0.75rem',
            }}
          >
            {description}
          </Typography>
        </Box>
      </CardContent>
    </Card>
  );
};

// Activity Item Component
const ActivityItem = ({ icon, title, description, time, status }) => {
  const theme = useTheme();
  
  const getStatusColor = (status) => {
    switch (status) {
      case 'completed': return theme.palette.success.main;
      case 'pending': return theme.palette.warning.main;
      case 'failed': return theme.palette.error.main;
      default: return theme.palette.info.main;
    }
  };

  return (
    <ListItem sx={{ px: 0, py: 2 }}>
      <ListItemIcon>
        <Avatar
          sx={{
            width: 40,
            height: 40,
            backgroundColor: alpha(getStatusColor(status), 0.1),
            color: getStatusColor(status),
          }}
        >
          {icon}
        </Avatar>
      </ListItemIcon>
      <ListItemText
        primary={
          <Typography variant="body1" sx={{ fontWeight: 500, mb: 0.5 }}>
            {title}
          </Typography>
        }
        secondary={
          <Box>
            <Typography variant="body2" color="text.secondary" sx={{ mb: 0.5 }}>
              {description}
            </Typography>
            <Typography variant="caption" color="text.secondary">
              {time}
            </Typography>
          </Box>
        }
      />
      <Chip
        size="small"
        label={status}
        sx={{
          backgroundColor: alpha(getStatusColor(status), 0.1),
          color: getStatusColor(status),
          fontWeight: 500,
          textTransform: 'capitalize',
        }}
      />
    </ListItem>
  );
};

const ModernDashboard = () => {
  const theme = useTheme();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate API call
    const fetchData = async () => {
      await new Promise(resolve => setTimeout(resolve, 1000));
      setDashboardData({
        projects: { total: 24, change: '+12%', trend: 'up' },
        carbonCredits: { total: '1,547', change: '+8%', trend: 'up' },
        verification: { total: '89%', change: '+3%', trend: 'up' },
        revenue: { total: '$45,672', change: '-2%', trend: 'down' },
      });
      setLoading(false);
    };

    fetchData();
  }, []);

  const chartData = {
    labels: ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'],
    datasets: [
      {
        label: 'Carbon Credits Generated',
        data: [120, 190, 300, 500, 200, 300],
        borderColor: '#4F46E5',
        backgroundColor: 'rgba(79, 70, 229, 0.1)',
        tension: 0.4,
        fill: true,
      },
      {
        label: 'Projects Completed',
        data: [80, 120, 200, 350, 150, 250],
        borderColor: '#06B6D4',
        backgroundColor: 'rgba(6, 182, 212, 0.1)',
        tension: 0.4,
        fill: true,
      },
    ],
  };

  const doughnutData = {
    labels: ['Active', 'Completed', 'Pending', 'Rejected'],
    datasets: [
      {
        data: [45, 30, 20, 5],
        backgroundColor: ['#10B981', '#4F46E5', '#F59E0B', '#EF4444'],
        borderWidth: 0,
      },
    ],
  };

  const statCards = [
    {
      title: 'Active Projects',
      value: dashboardData?.projects.total || '0',
      change: dashboardData?.projects.change || '0%',
      trend: dashboardData?.projects.trend || 'up',
      icon: <ProjectIcon />,
      gradient: '#4F46E5, #3730A3',
      description: 'vs last month',
    },
    {
      title: 'Carbon Credits',
      value: dashboardData?.carbonCredits.total || '0',
      change: dashboardData?.carbonCredits.change || '0%',
      trend: dashboardData?.carbonCredits.trend || 'up',
      icon: <EcoIcon />,
      gradient: '#10B981, #047857',
      description: 'tons CO2',
    },
    {
      title: 'Verification Rate',
      value: dashboardData?.verification.total || '0%',
      change: dashboardData?.verification.change || '0%',
      trend: dashboardData?.verification.trend || 'up',
      icon: <VerificationIcon />,
      gradient: '#06B6D4, #0891B2',
      description: 'success rate',
    },
    {
      title: 'Total Revenue',
      value: dashboardData?.revenue.total || '$0',
      change: dashboardData?.revenue.change || '0%',
      trend: dashboardData?.revenue.trend || 'down',
      icon: <MoneyIcon />,
      gradient: '#8B5CF6, #6D28D9',
      description: 'this month',
    },
  ];

  const recentProjects = [
    { id: 1, name: 'Mangrove Restoration - Kerala', status: 'active', progress: 75, location: 'Kerala Coast' },
    { id: 2, name: 'Seagrass Conservation - Tamil Nadu', status: 'completed', progress: 100, location: 'Tamil Nadu' },
    { id: 3, name: 'Coral Reef Protection - Andaman', status: 'pending', progress: 45, location: 'Andaman Islands' },
    { id: 4, name: 'Wetland Restoration - Odisha', status: 'active', progress: 60, location: 'Odisha Coast' },
  ];

  const recentActivities = [
    {
      icon: <CheckCircle />,
      title: 'Project Verification Completed',
      description: 'Mangrove restoration project verified successfully',
      time: '2 hours ago',
      status: 'completed',
    },
    {
      icon: <Schedule />,
      title: 'New Credit Certificate Issued',
      description: '150 tons CO2 credits added to marketplace',
      time: '4 hours ago',
      status: 'pending',
    },
    {
      icon: <Warning />,
      title: 'Verification Pending',
      description: 'Seagrass project requires additional documentation',
      time: '6 hours ago',
      status: 'failed',
    },
    {
      icon: <Notifications />,
      title: 'Monthly Report Generated',
      description: 'Carbon sequestration report for May 2024',
      time: '1 day ago',
      status: 'completed',
    },
  ];

  if (loading) {
    return (
      <Box
        sx={{
          ml: `${SIDEBAR_WIDTH}px`,
          p: 3,
          minHeight: '100vh',
          background: theme.palette.background.default,
        }}
      >
        <Typography>Loading...</Typography>
      </Box>
    );
  }

  return (
    <Box
      sx={{
        ml: `${SIDEBAR_WIDTH}px`,
        mt: '80px', // Top bar height
        p: 3,
        minHeight: '100vh',
        background: theme.palette.background.default,
      }}
    >
      {/* Stats Cards */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        {statCards.map((card, index) => (
          <Grid item xs={12} sm={6} lg={3} key={index}>
            <StatCard {...card} />
          </Grid>
        ))}
      </Grid>

      {/* Main Content Grid */}
      <Grid container spacing={3}>
        {/* Charts Section */}
        <Grid item xs={12} lg={8}>
          <Card sx={{ mb: 3 }}>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h5" sx={{ fontWeight: 600, mb: 3 }}>
                Carbon Credits & Project Trends
              </Typography>
              <Box sx={{ height: 300 }}>
                <Line
                  data={chartData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        position: 'top',
                      },
                    },
                    scales: {
                      y: {
                        beginAtZero: true,
                        grid: {
                          color: alpha(theme.palette.text.secondary, 0.1),
                        },
                      },
                      x: {
                        grid: {
                          color: alpha(theme.palette.text.secondary, 0.1),
                        },
                      },
                    },
                  }}
                />
              </Box>
            </CardContent>
          </Card>

          {/* Recent Projects Table */}
          <Card>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h5" sx={{ fontWeight: 600, mb: 3 }}>
                Recent Projects
              </Typography>
              <TableContainer>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell sx={{ fontWeight: 600 }}>Project Name</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Location</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Status</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Progress</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {recentProjects.map((project) => (
                      <TableRow key={project.id} hover>
                        <TableCell>
                          <Typography variant="body1" sx={{ fontWeight: 500 }}>
                            {project.name}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2" color="text.secondary">
                            {project.location}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Chip
                            label={project.status}
                            size="small"
                            sx={{
                              textTransform: 'capitalize',
                              fontWeight: 500,
                              ...(project.status === 'active' && {
                                backgroundColor: alpha(theme.palette.success.main, 0.1),
                                color: theme.palette.success.main,
                              }),
                              ...(project.status === 'completed' && {
                                backgroundColor: alpha(theme.palette.info.main, 0.1),
                                color: theme.palette.info.main,
                              }),
                              ...(project.status === 'pending' && {
                                backgroundColor: alpha(theme.palette.warning.main, 0.1),
                                color: theme.palette.warning.main,
                              }),
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <Box sx={{ display: 'flex', alignItems: 'center' }}>
                            <Box sx={{ width: '100%', mr: 1 }}>
                              <LinearProgress
                                variant="determinate"
                                value={project.progress}
                                sx={{
                                  height: 8,
                                  borderRadius: 4,
                                  '& .MuiLinearProgress-bar': {
                                    borderRadius: 4,
                                  },
                                }}
                              />
                            </Box>
                            <Typography variant="body2" color="text.secondary">
                              {project.progress}%
                            </Typography>
                          </Box>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* Right Sidebar */}
        <Grid item xs={12} lg={4}>
          {/* Project Distribution */}
          <Card sx={{ mb: 3 }}>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 3 }}>
                Project Distribution
              </Typography>
              <Box sx={{ height: 200, display: 'flex', justifyContent: 'center' }}>
                <Doughnut
                  data={doughnutData}
                  options={{
                    responsive: true,
                    maintainAspectRatio: false,
                    plugins: {
                      legend: {
                        position: 'bottom',
                      },
                    },
                  }}
                />
              </Box>
            </CardContent>
          </Card>

          {/* Recent Activity */}
          <Card>
            <CardContent sx={{ p: 3 }}>
              <Typography variant="h6" sx={{ fontWeight: 600, mb: 2 }}>
                Recent Activity
              </Typography>
              <List sx={{ p: 0 }}>
                {recentActivities.map((activity, index) => (
                  <React.Fragment key={index}>
                    <ActivityItem {...activity} />
                    {index < recentActivities.length - 1 && (
                      <Divider sx={{ my: 1 }} />
                    )}
                  </React.Fragment>
                ))}
              </List>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Box>
  );
};

export default ModernDashboard;
