import React, { useState, useRef, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Typography,
  Box,
  TextField,
  Button,
  Grid,
  MenuItem,
  Stepper,
  Step,
  StepLabel,
  Card,
  CardContent,
  Alert,
  CircularProgress,
  Paper,
  IconButton,
  Chip,
  Divider,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  InputAdornment,
  LinearProgress,
} from '@mui/material';
import {
  PhotoCamera,
  VideoCall,
  AudioFile,
  GpsFixed,
  CloudUpload,
  Delete,
  Preview,
  Nature,
  Water,
  Thermostat,
  Speed,
  Analytics,
  Blockchain,
  Storage,
} from '@mui/icons-material';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import { projectAPI, handleAPIError } from '../services/api';
import { useSnackbar } from 'notistack';

const LocationPicker = ({ position, setPosition }) => {
  useMapEvents({
    click(e) {
      setPosition({
        lat: e.latlng.lat,
        lng: e.latlng.lng,
      });
    },
  });

  return position ? <Marker position={[position.lat, position.lng]} /> : null;
};

const ProjectCreate = () => {
  const navigate = useNavigate();
  const { enqueueSnackbar } = useSnackbar();
  const [activeStep, setActiveStep] = useState(0);
  const [loading, setLoading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [previewDialog, setPreviewDialog] = useState({ open: false, file: null });

  // File input refs
  const photoInputRef = useRef(null);
  const videoInputRef = useRef(null);
  const audioInputRef = useRef(null);
  const documentInputRef = useRef(null);

  const [formData, setFormData] = useState({
    // Basic project info
    project_name: '',
    location: { lat: 19.0760, lng: 72.8777 }, // Default to Mumbai
    area_hectares: '',
    ecosystem_type: '',
    restoration_method: '',
    community_details: '',
    contact_email: '',
    phone_number: '',

    // Media files with geo-tagging
    media_files: {
      photos: [],
      videos: [],
      audio_recordings: [],
      documents: [],
    },

    // Field measurements
    field_measurements: {
      water_quality: {
        ph_level: '',
        salinity: '',
        temperature: '',
        dissolved_oxygen: '',
      },
      soil_analysis: {
        carbon_content: '',
        nitrogen_level: '',
        phosphorus_level: '',
        moisture_content: '',
      },
      biodiversity: {
        species_count: '',
        vegetation_density: '',
        wildlife_observations: '',
      },
      environmental: {
        tide_level: '',
        weather_conditions: '',
        visibility: '',
      }
    },

    // GPS tracking
    gps_data: {
      waypoints: [],
      track_log: [],
      boundary_coordinates: [],
    },

    // IPFS and blockchain
    ipfs_hashes: [],
    blockchain_tx_hash: '',
  });

  const ecosystemTypes = [
    'mangrove',
    'seagrass',
    'salt_marsh',
    'coastal_wetland',
    'coral_reef',
    'mudflat',
  ];

  const restorationMethods = [
    'Natural regeneration',
    'Assisted regeneration',
    'Active restoration',
    'Hybrid approach',
    'Community-based restoration',
    'Scientific intervention',
  ];

  const steps = [
    'Project Details', 
    'Location & GPS', 
    'Media Upload', 
    'Field Measurements', 
    'Community Info', 
    'Blockchain & IPFS',
    'Review & Submit'
  ];

  const handleInputChange = (field) => (event) => {
    setFormData(prev => ({
      ...prev,
      [field]: event.target.value,
    }));
  };

  const handleLocationChange = (newPosition) => {
    setFormData(prev => ({
      ...prev,
      location: newPosition,
    }));
  };

  const validateStep = (step) => {
    switch (step) {
      case 0:
        return formData.project_name && formData.ecosystem_type && formData.restoration_method;
      case 1:
        return formData.area_hectares && formData.location.lat && formData.location.lng;
      case 2:
        return formData.community_details && formData.contact_email && formData.phone_number;
      default:
        return true;
    }
  };

  const handleNext = () => {
    if (validateStep(activeStep)) {
      setActiveStep(prev => prev + 1);
    } else {
      enqueueSnackbar('Please fill in all required fields', { variant: 'warning' });
    }
  };

  const handleBack = () => {
    setActiveStep(prev => prev - 1);
  };

  const handleSubmit = async () => {
    try {
      setLoading(true);

      const projectData = {
        ...formData,
        area_hectares: parseFloat(formData.area_hectares),
      };

      await projectAPI.create(projectData);

      enqueueSnackbar('Project created successfully!', { variant: 'success' });
      navigate('/projects');

    } catch (error) {
      const errorInfo = handleAPIError(error);
      enqueueSnackbar(errorInfo.message, { variant: 'error' });
    } finally {
      setLoading(false);
    }
  };

  const renderStepContent = () => {
    switch (activeStep) {
      case 0:
        return (
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Project Name"
                value={formData.project_name}
                onChange={handleInputChange('project_name')}
                required
                placeholder="e.g., Coastal Mangrove Restoration Project"
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                select
                label="Ecosystem Type"
                value={formData.ecosystem_type}
                onChange={handleInputChange('ecosystem_type')}
                required
              >
                {ecosystemTypes.map((type) => (
                  <MenuItem key={type} value={type}>
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                select
                label="Restoration Method"
                value={formData.restoration_method}
                onChange={handleInputChange('restoration_method')}
                required
              >
                {restorationMethods.map((method) => (
                  <MenuItem key={method} value={method}>
                    {method}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>
          </Grid>
        );

      case 1:
        return (
          <Grid container spacing={3}>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                type="number"
                label="Area (Hectares)"
                value={formData.area_hectares}
                onChange={handleInputChange('area_hectares')}
                required
                inputProps={{ min: 0, step: 0.1 }}
              />
              <Box sx={{ mt: 2 }}>
                <Typography variant="body2" color="textSecondary">
                  Current Location: {formData.location.lat.toFixed(6)}, {formData.location.lng.toFixed(6)}
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={12} md={6}>
              <Box sx={{ height: 300, border: '1px solid #ccc', borderRadius: 1 }}>
                <MapContainer
                  center={[formData.location.lat, formData.location.lng]}
                  zoom={13}
                  style={{ height: '100%', width: '100%' }}
                >
                  <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                  />
                  <LocationPicker 
                    position={formData.location} 
                    setPosition={handleLocationChange}
                  />
                </MapContainer>
              </Box>
              <Typography variant="caption" color="textSecondary" sx={{ mt: 1, display: 'block' }}>
                Click on the map to set project location
              </Typography>
            </Grid>
          </Grid>
        );

      case 2:
        return (
          <Grid container spacing={3}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                multiline
                rows={4}
                label="Community Details"
                value={formData.community_details}
                onChange={handleInputChange('community_details')}
                required
                placeholder="Describe the local communities involved, their role, and expected benefits..."
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                type="email"
                label="Contact Email"
                value={formData.contact_email}
                onChange={handleInputChange('contact_email')}
                required
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Phone Number"
                value={formData.phone_number}
                onChange={handleInputChange('phone_number')}
                required
                placeholder="+91 XXXXX XXXXX"
              />
            </Grid>
          </Grid>
        );

      case 3:
        return (
          <Box>
            <Alert severity="info" sx={{ mb: 3 }}>
              Please review your project details before submission. Once submitted, the project will be sent to NCCR for review and approval.
            </Alert>
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <Typography variant="h6" gutterBottom>Project Information</Typography>
                <Typography><strong>Name:</strong> {formData.project_name}</Typography>
                <Typography><strong>Ecosystem:</strong> {formData.ecosystem_type}</Typography>
                <Typography><strong>Method:</strong> {formData.restoration_method}</Typography>
                <Typography><strong>Area:</strong> {formData.area_hectares} hectares</Typography>
              </Grid>
              <Grid item xs={12} md={6}>
                <Typography variant="h6" gutterBottom>Contact Information</Typography>
                <Typography><strong>Email:</strong> {formData.contact_email}</Typography>
                <Typography><strong>Phone:</strong> {formData.phone_number}</Typography>
                <Typography><strong>Location:</strong> {formData.location.lat.toFixed(4)}, {formData.location.lng.toFixed(4)}</Typography>
              </Grid>
              <Grid item xs={12}>
                <Typography variant="h6" gutterBottom>Community Details</Typography>
                <Typography>{formData.community_details}</Typography>
              </Grid>
            </Grid>
          </Box>
        );

      default:
        return null;
    }
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Create New Blue Carbon Project
      </Typography>

      <Card>
        <CardContent>
          <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
            {steps.map((label) => (
              <Step key={label}>
                <StepLabel>{label}</StepLabel>
              </Step>
            ))}
          </Stepper>

          {renderStepContent()}

          <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
            <Button
              disabled={activeStep === 0}
              onClick={handleBack}
            >
              Back
            </Button>

            <Box>
              {activeStep === steps.length - 1 ? (
                <Button
                  variant="contained"
                  onClick={handleSubmit}
                  disabled={loading}
                  startIcon={loading ? <CircularProgress size={20} /> : null}
                >
                  {loading ? 'Submitting...' : 'Submit Project'}
                </Button>
              ) : (
                <Button
                  variant="contained"
                  onClick={handleNext}
                  disabled={!validateStep(activeStep)}
                >
                  Next
                </Button>
              )}
            </Box>
          </Box>
        </CardContent>
      </Card>
    </Box>
  );
};

export default ProjectCreate;
