import React from 'react';
import { 
  Box, 
  Drawer, 
  List, 
  ListItem, 
  ListItemButton, 
  ListItemIcon, 
  ListItemText,
  Typography,
  Divider,
  Avatar,
  useTheme,
} from '@mui/material';
import { alpha } from '@mui/material/styles';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  Add as AddIcon,
  PhotoCamera as PhotoIcon,
  Store as MarketIcon,
  AccountBalance as ProjectIcon,
  Verified as VerifiedIcon,
  Payment as PaymentIcon,
  Assessment as ReportsIcon,
  Dashboard as DashboardIcon,
  Timeline as TimelineIcon,
} from '@mui/icons-material';

const SIDEBAR_WIDTH = 280;

const UserSidebar = ({ open, onClose }) => {
  const theme = useTheme();
  const navigate = useNavigate();
  const location = useLocation();

  const quickActions = [
    {
      title: 'Dashboard',
      description: 'Overview and statistics',
      icon: <DashboardIcon />,
      path: '/dashboard',
      color: theme.palette.primary.main,
    },
    {
      title: 'Create Project',
      description: 'Start new blue carbon project',
      icon: <AddIcon />,
      path: '/projects/create',
      color: theme.palette.secondary.main,
    },
    {
      title: 'Project Tracker',
      description: 'Track verification pipeline',
      icon: <TimelineIcon />,
      path: '/projects/tracker',
      color: theme.palette.info.main,
      external: true,
      url: 'http://localhost:8005',
    },
    {
      title: 'NGO Verification',
      description: 'Third-party verification',
      icon: <VerifiedIcon />,
      path: '/verification',
      color: theme.palette.success.main,
      external: true,
      url: 'http://localhost:8004',
    },
    {
      title: 'Data Collection',
      description: 'Field data and measurements',
      icon: <PhotoIcon />,
      path: '/data-collection',
      color: theme.palette.warning.main,
    },
    {
      title: 'Marketplace',
      description: 'Trade carbon credits',
      icon: <MarketIcon />,
      path: '/marketplace',
      color: '#FF6B35',
    },
    {
      title: 'Carbon Credits',
      description: 'Manage tokenized credits',
      icon: <ProjectIcon />,
      path: '/carbon-credits',
      color: '#7C3AED',
    },
    {
      title: 'Payments',
      description: 'Transfer blockchain credits',
      icon: <PaymentIcon />,
      path: '/payments',
      color: theme.palette.success.main,
    },
    {
      title: 'Reports',
      description: 'Analytics and insights',
      icon: <ReportsIcon />,
      path: '/reports',
      color: '#DC2626',
    },
  ];

  const handleNavigation = (action) => {
    if (action.external) {
      window.open(action.url, '_blank');
    } else {
      navigate(action.path);
    }
    if (onClose) onClose();
  };

  const isActive = (path) => {
    return location.pathname === path || 
           (path === '/dashboard' && (location.pathname === '/' || location.pathname === '/dashboard'));
  };

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: SIDEBAR_WIDTH,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: SIDEBAR_WIDTH,
          boxSizing: 'border-box',
          backgroundColor: theme.palette.background.sidebar,
          color: theme.palette.text.sidebar,
          border: 'none',
          boxShadow: '2px 0 8px rgba(0, 0, 0, 0.1)',
        },
      }}
    >
      <Box sx={{ overflow: 'auto', height: '100%' }}>
        {/* Header */}
        <Box sx={{ p: 3, textAlign: 'center' }}>
          <Avatar
            sx={{
              width: 56,
              height: 56,
              mx: 'auto',
              mb: 2,
              backgroundColor: theme.palette.primary.main,
              fontSize: '1.5rem',
            }}
          >
            ðŸŒŠ
          </Avatar>
          <Typography variant="h6" fontWeight={600} color="inherit">
            Blue Carbon MRV
          </Typography>
          <Typography variant="body2" sx={{ color: theme.palette.text.sidebarSecondary, mt: 0.5 }}>
            Community Dashboard
          </Typography>
        </Box>

        <Divider sx={{ borderColor: alpha('#FFFFFF', 0.12) }} />

        {/* Quick Actions */}
        <Box sx={{ p: 2 }}>
          <Typography 
            variant="subtitle2" 
            sx={{ 
              px: 2, 
              py: 1, 
              color: theme.palette.text.sidebarSecondary,
              textTransform: 'uppercase',
              letterSpacing: '0.5px',
              fontSize: '0.75rem',
              fontWeight: 600,
            }}
          >
            Quick Actions
          </Typography>
          
          <List sx={{ pt: 0 }}>
            {quickActions.map((action, index) => (
              <ListItem key={index} disablePadding sx={{ mb: 0.5 }}>
                <ListItemButton
                  onClick={() => handleNavigation(action)}
                  sx={{
                    borderRadius: '12px',
                    mx: 1,
                    minHeight: 56,
                    backgroundColor: isActive(action.path) 
                      ? alpha(theme.palette.primary.main, 0.15)
                      : 'transparent',
                    border: isActive(action.path) 
                      ? `1px solid ${alpha(theme.palette.primary.main, 0.3)}`
                      : '1px solid transparent',
                    '&:hover': {
                      backgroundColor: alpha('#FFFFFF', 0.08),
                      transform: 'translateX(4px)',
                      transition: 'all 0.2s ease-in-out',
                    },
                    transition: 'all 0.2s ease-in-out',
                  }}
                >
                  <ListItemIcon sx={{ minWidth: 44 }}>
                    <Avatar
                      sx={{
                        width: 36,
                        height: 36,
                        backgroundColor: isActive(action.path) 
                          ? action.color 
                          : alpha(action.color, 0.2),
                        color: isActive(action.path) ? '#FFFFFF' : action.color,
                        fontSize: '1rem',
                      }}
                    >
                      {action.icon}
                    </Avatar>
                  </ListItemIcon>
                  <ListItemText
                    primary={
                      <Typography variant="subtitle2" fontWeight={isActive(action.path) ? 600 : 500}>
                        {action.title}
                      </Typography>
                    }
                    secondary={
                      <Typography 
                        variant="caption" 
                        sx={{ 
                          color: theme.palette.text.sidebarSecondary,
                          fontSize: '0.75rem',
                        }}
                      >
                        {action.description}
                      </Typography>
                    }
                  />
                  {action.external && (
                    <Box
                      sx={{
                        width: 8,
                        height: 8,
                        borderRadius: '50%',
                        backgroundColor: theme.palette.success.main,
                        ml: 1,
                      }}
                    />
                  )}
                </ListItemButton>
              </ListItem>
            ))}
          </List>
        </Box>

        {/* Status Section */}
        <Box sx={{ p: 2, mt: 'auto' }}>
          <Divider sx={{ borderColor: alpha('#FFFFFF', 0.12), mb: 2 }} />
          
          <Box
            sx={{
              p: 2,
              borderRadius: '12px',
              backgroundColor: alpha(theme.palette.success.main, 0.1),
              border: `1px solid ${alpha(theme.palette.success.main, 0.2)}`,
            }}
          >
            <Typography variant="subtitle2" fontWeight={600} sx={{ mb: 1 }}>
              ðŸ”— System Status
            </Typography>
            <Typography variant="caption" sx={{ color: theme.palette.text.sidebarSecondary }}>
              All services operational
            </Typography>
            <Box sx={{ display: 'flex', alignItems: 'center', mt: 1, gap: 1 }}>
              <Box
                sx={{
                  width: 6,
                  height: 6,
                  borderRadius: '50%',
                  backgroundColor: theme.palette.success.main,
                }}
              />
              <Typography variant="caption" sx={{ color: theme.palette.text.sidebarSecondary }}>
                Backend Connected
              </Typography>
            </Box>
          </Box>
        </Box>
      </Box>
    </Drawer>
  );
};

export default UserSidebar;
export { SIDEBAR_WIDTH };
